package Y0119;
class Total{
	int a, b;
	Total(int a, int b){
		this.a=a;
		this.b=b;
	}
	void sum(){
		int sum=0;
		for (int i=a;i>=a;a++) {
			sum+=i;
		}
		System.out.println(sum);
	}
	
}
public class Y0119_prc3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		Total t = new Total(1,10);
		t.sum();
	}

}
